import{_ as t,o as e,c as l,e as d}from"./app-e85d5a28.js";const a={},i=d('<h1 id="术语表" tabindex="-1"><a class="header-anchor" href="#术语表" aria-hidden="true">#</a> 术语表</h1><h2 id="硬件术语" tabindex="-1"><a class="header-anchor" href="#硬件术语" aria-hidden="true">#</a> 硬件术语</h2><table><thead><tr><th style="text-align:left;">术语</th><th style="text-align:left;">解释说明</th></tr></thead><tbody><tr><td style="text-align:left;">sunxi</td><td style="text-align:left;">指 Allwinner 的一系列 SOC 硬件平台</td></tr><tr><td style="text-align:left;">M33 Star</td><td style="text-align:left;">基于 ARMv8-M 架构 32 位元微处理器单元</td></tr><tr><td style="text-align:left;">C906</td><td style="text-align:left;">平头哥推出的一款 RISC-V 架构的 64 位元处理器单元</td></tr><tr><td style="text-align:left;">HIFI5</td><td style="text-align:left;">Cadence 推出的 Cadence Tensilica HiFi 5 DSP 32 位处理器单元</td></tr><tr><td style="text-align:left;">R128</td><td style="text-align:left;">全志推出的一款包含 M33+C906+HIFI5 处理器的 SoC 平台</td></tr><tr><td style="text-align:left;">AudioCodec</td><td style="text-align:left;">芯片内置音频接口</td></tr><tr><td style="text-align:left;">I2S/PCM</td><td style="text-align:left;">外置数字音频接口，常用于外接codec 模块。</td></tr><tr><td style="text-align:left;">AHUB</td><td style="text-align:left;">音频集线器，内部集成I2S 接口及DAM 混音器，可实现多路输入播放及硬件混音功能</td></tr><tr><td style="text-align:left;">S/PDIF</td><td style="text-align:left;">外置音响音频设备接口，一般使用同轴电缆或光纤接口。</td></tr><tr><td style="text-align:left;">DMIC</td><td style="text-align:left;">外置数字MIC 接口</td></tr><tr><td style="text-align:left;">MAD</td><td style="text-align:left;">Mic Activity Detector, 语音能量检测模块</td></tr><tr><td style="text-align:left;">同源播放</td><td style="text-align:left;">不同音频模块同时播放同一份音频数据</td></tr><tr><td style="text-align:left;">同步采样</td><td style="text-align:left;">不同音频模块同时录音（可消除线程调度时差影响）</td></tr></tbody></table><h2 id="软件术语" tabindex="-1"><a class="header-anchor" href="#软件术语" aria-hidden="true">#</a> 软件术语</h2><table><thead><tr><th style="text-align:left;">术语</th><th style="text-align:left;">解释说明</th></tr></thead><tbody><tr><td style="text-align:left;">FreeRTOS</td><td style="text-align:left;">一种开源的实时操作系统</td></tr><tr><td style="text-align:left;">gcc</td><td style="text-align:left;">GNU 开发的编程语言编译器。用于编译 M33 与 C906 上运行的程序</td></tr><tr><td style="text-align:left;">xcc</td><td style="text-align:left;">Xtensa C/C++ 编译器。用于编译在 HIFI5 运行的程序</td></tr><tr><td style="text-align:left;">hwspinlock</td><td style="text-align:left;">在多核系统中，hwspinlock 提供一种硬件锁同步机制。</td></tr><tr><td style="text-align:left;">ALSA</td><td style="text-align:left;">Advanced Linux Sound Architecture</td></tr><tr><td style="text-align:left;">ASoC</td><td style="text-align:left;">ALSA System on Chip</td></tr><tr><td style="text-align:left;">DAPM</td><td style="text-align:left;">动态音频电源管理</td></tr><tr><td style="text-align:left;">samplebit</td><td style="text-align:left;">样本精度，记录音频数据最基本的单位，常见的有 16 位</td></tr><tr><td style="text-align:left;">channel</td><td style="text-align:left;">通道数，该参数为 1 表示单声道，2 表示立体声，大于 2 表示多声道</td></tr><tr><td style="text-align:left;">rate</td><td style="text-align:left;">采样率，每秒钟采样次数，该次数是针对帧而言。</td></tr><tr><td style="text-align:left;">frame</td><td style="text-align:left;">帧，记录了一个声音单元，其长度为样本长度与通道数的乘积。</td></tr><tr><td style="text-align:left;">period size</td><td style="text-align:left;">每次硬件中断处理音频数据的帧数。</td></tr><tr><td style="text-align:left;">period count</td><td style="text-align:left;">处理完一个 buffer 数据所需的硬件中断次数。</td></tr><tr><td style="text-align:left;">buffer size</td><td style="text-align:left;">数据缓冲区大小 (period size * period count)</td></tr><tr><td style="text-align:left;">DRC</td><td style="text-align:left;">音频输出动态范围控制</td></tr><tr><td style="text-align:left;">HPF</td><td style="text-align:left;">高通滤波</td></tr><tr><td style="text-align:left;">XRUN</td><td style="text-align:left;">音频流异常状态，分为 underrun 和 overrun 两种状态。</td></tr><tr><td style="text-align:left;">交错模式</td><td style="text-align:left;">一种音频数据记录模式，数据以连续帧形式存放 (帧 1_L, 帧 1_R, 帧 2_L, 帧 2_R, —)。</td></tr><tr><td style="text-align:left;">非交错模式</td><td style="text-align:left;">一种音频数据记录模式，数据是以连续通道形式存放 (L-帧 1, L-帧 2, —, R-帧 2, R-帧 2, —)。</td></tr><tr><td style="text-align:left;">tinyalsa</td><td style="text-align:left;">在 Linux/RTOS 内核中与 ALSA 接口对接的库，可用于基本播录。</td></tr><tr><td style="text-align:left;">alsalib</td><td style="text-align:left;">在 Linux/RTOS 内核中与 ALSA 接口对接的库，可用于基本播录，并可与常见音频算法组合使用。</td></tr></tbody></table>',5),r=[i];function n(s,f){return e(),l("div",null,r)}const y=t(a,[["render",n],["__file","chapter2.html.vue"]]);export{y as default};
